/**
 * Check if a city exists in Clalit's system
 */
export function isCityValid(city: string): boolean {
  return ISRAEL_CITIES.includes(city);
}

/**
 * Find similar cities (for fuzzy matching)
 */
export function findSimilarCities(input: string): string[] {
  const normalized = input.trim();
  return ISRAEL_CITIES.filter(city => 
    city.includes(normalized) || normalized.includes(city)
  );
}

/**
 * Get coordinates for a city (if pre-cached)
 */
export function getCityCoordinates(city: string): { lat: number; lon: number } | null {
  return CITY_COORDINATES[city] || null;
}
